<script setup>
import {computed} from 'vue'
import {getBookById} from '@/services/books.js'
import {useUserStore} from '@/services/user.js'
import Author from '@/components/Author.vue'
import FramePlaque from '@/components/FramePlaque.vue'
import FramePicture from '@/components/FramePicture.vue'
import Footer from '@/components/Footer.vue'
import IconRead from '@/components/IconRead.vue'
import IconSebooks from '@/components/IconSebooks.vue'
import IconStar from '@/components/IconStar.vue'

const props = defineProps({
  bookId: {type: String, default: ''},
})

console.log('BookItem')

const userStore = useUserStore()

const book = computed(() => {
  return getBookById(props.bookId)
})

const readingEaseDescription = computed(() => {
  let value = book.value.readingEase
  if (!value) return ''
  if (value >= 90) return 'very easy'
  if (value >= 79) return 'easy'
  if (value >= 69) return 'fairly easy'
  if (value >= 59) return 'average difficulty'
  if (value >= 49) return 'fairly difficult'
  if (value >= 39) return 'difficult'
  return 'very difficult'
})
</script>

<template>
  <div v-if="!book.id" class="p-4">
    <h1 class="">
      404 Book not found
    </h1>
  </div>

  <div v-else class="the-book">
    <!-- Book spine -->
    <div class="book-spine p-4 h-full flex flex-col items-center border-x dark:border-x-gray-600">
      <div class="btn-get mt-1 mb-10">
        <a
          :href="book.url"
          class="no-underline text-black dark:text-yellow-100 bg-white dark:bg-gray-800 hover:bg-yellow-50 dark:hover:bg-gray-900"
          target="_blank"
        >
          <icon-sebooks class="px-4" />
          <div class="text-sm">
            Get the book
          </div>
        </a>
        <div class="btn-get-decoration" />
      </div>

      <!-- Mark and Read -->
      <div
        class="text-center w-24 mb-2 p-2 cursor-pointer rounded hover:bg-yellow-50 dark:hover:bg-gray-600"
        @click="userStore.toggleFavorite(book.id)"
      >
        <icon-star :filled="userStore.favorites.includes(book.id)" class="mx-auto w-8 h-8" />
        <div class="text-sm">
          Favorite
        </div>
      </div>
      <div
        class="text-center w-24 p-2 cursor-pointer rounded hover:bg-yellow-50 dark:hover:bg-gray-600"
        @click="userStore.toggleFinished(book.id)"
      >
        <icon-read :filled="userStore.finished.includes(book.id)" class="mx-auto w-8 h-8" />
        <div class="text-sm">
          Finished
        </div>
      </div>

      <!-- Vertical title -->
      <div class="hidden rotate-90 origin-left whitespace-nowrap absolute top-72 left-16">
        <div class="font-bold text-yellow-300 dark:text-yellow-800">
          {{ book.creator }}
        </div>
        <div class="text-4xl font-bold text-yellow-300 dark:text-yellow-900">
          {{ book.title }}
        </div>
      </div>
    </div>

    <!-- Book cover -->
    <div class="book-cover">
      <div class="book-title text-center">
        <Author
          :full-name="book.authorFullName"
          :name="book.creator"
          class="pt-4 pb-2 justify-center"
        />
        <h1 class="opacity-80">
          {{ book.title }}
        </h1>
      </div>

      <div class="book-tags mb-4 flex justify-center space-x-2">
        <FramePlaque
          v-for="(tag, i) in book.tags"
          :key="`tag-${i}`"
          :tag="tag"
        />
      </div>

      <div class="book-counts w-full max-w-desc justify-self-end text-sm">
        {{ Number(book.wordsCount).toLocaleString() }}
        words,
        {{ readingEaseDescription }}
        <span class="text-gray-500">({{ Math.round(book.readingEase) }})</span>
      </div>

      <div class="book-subj w-full max-w-desc justify-self-end text-indigo-700 dark:text-indigo-300 text-sm">
        <div
          v-for="(subject, i) in book.subject"
          :key="`subject-${i}`"
        >
          {{ subject.replace(/--/g, '—') }}
        </div>
      </div>

      <div class="book-desc w-full max-w-desc justify-self-end" v-html="book.description" />

      <div class="book-pic">
        <FramePicture :book-id="book.id" />
      </div>

      <div class="book-footer">
        <Footer class="mt-4" />
      </div>
    </div>
  </div>
</template>

